def convert_to_uppercase(event, context):
    """
    将输入文本转换为大写
    """
    # 检查输入事件是否包含text字段
    if not isinstance(event, dict):
        return {
            "error": "输入必须是字典格式",
            "success": False
        }

    text = event.get('text', '')

    if not text:
        return {
            "error": "缺少text字段或text为空",
            "success": False
        }

    # 转换为大写
    uppercase_text = text.upper()

    # 统计信息
    original_length = len(text)
    char_count = len([c for c in text if c.isalpha()])
    digit_count = len([c for c in text if c.isdigit()])
    space_count = text.count(' ')

    return {
        "success": True,
        "original_text": text,
        "uppercase_text": uppercase_text,
        "statistics": {
            "total_length": original_length,
            "letter_count": char_count,
            "digit_count": digit_count,
            "space_count": space_count
        },
        "processed_by": f"Python函数 (用户: {context.get('user', 'unknown')})",
        "operation": "大写转换"
    }
